*/
pragma solidity 0.5.2;
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Mintable.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Burnable.sol";
/**
* @author Balaji Pachai
* @title Asset20Token
* @dev Escrow that is intended to facilitate the trade of REC721 tokens
*/


contract AssetToken is ERC20Mintable, ERC20Detailed, ERC20Burnable {
    /**
    * @dev Constructor.
    * @param name The name of the token to be minted.
    * @param symbol The symbol of the token to be minted.
    * @param decimals The decimals till which the token is divisible.
    * @param to The address to which the token will be minted and assigned.
    *@param value The number of tokens to be minted.
    */
    constructor (
        string memory name, string memory symbol, uint8 decimals, address to, uint256 value
    )
    public
    ERC20Detailed(name, symbol, decimals) {
        super.mint(to, value);
    }
}

