/**
* Create by Balaji on 08/16/2019
*/
pragma solidity 0.5.2;

import "./ERC20/AssetToken.sol";
import "./ERC721/ArtToken.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "./IFunderEscrow.sol";
import "./FunderEscrowDetailed.sol";
import "openzeppelin-solidity/contracts/token/ERC721/ERC721Holder.sol";
/**
* @author Balaji Pachai
* @title FunderEscrow
* @dev Contract that holds the details of all the funders
*/

contract FunderEscrow is Ownable, IFunderEscrow, FunderEscrowDetailed, ERC721Holder {
    using SafeMath for uint256;
    AssetToken public ASSET_TOKEN;
    ArtToken public ART_TOKEN;

    event FunderLinked(address indexed oldOwner, address indexed newOwner, uint timestamp);
    event PrintFunded(address indexed funder, uint indexed amount, uint timestamp);

    modifier hasContractExpired() {
        require(
            block.timestamp < EXPIRATION_TIMESTAMP,
                "in FunderEscrow:hasContractExpired(). The contract has not expired"
        );
        _;
    }

    /**
    * @dev Constructor
    */
    constructor(
        address payable _beneficiary,
        uint256 _pledgeAmount, uint256 _investorReturnOnPledgeAmountUponSale,
        uint256 _galleryFeeUponSale, uint256 _commission, uint256 _maxNumberOfPrints,
        uint256 _exitClauseAddPayment, uint256 _exitClausePayoutFromAel, uint256 _expTimestamp,
        address _commissioningGalleryAddr, address _salesCommissionAddr, address _sellingGalleryAddr,
        address addressNFT, address addressFT
    )
    public
    FunderEscrowDetailed(_beneficiary,_pledgeAmount, _investorReturnOnPledgeAmountUponSale, _galleryFeeUponSale,
        _commission, _maxNumberOfPrints, _exitClauseAddPayment, _exitClausePayoutFromAel, _expTimestamp,
        _commissioningGalleryAddr, _salesCommissionAddr, _sellingGalleryAddr
    )
    {
        require(addressNFT != address(0), "in FunderEscrow:constructor(). addressNFT cannot be address(0)");
        require(addressFT != address(0), "in FunderEscrow:constructor(). addressFT cannot be address(0)");

        ART_TOKEN = ArtToken(addressNFT);
        ASSET_TOKEN = AssetToken(addressFT);
    }

    // TODO @balaji Pass in ERC20 to the beneficiary to beneficairy as soon as funding takes place // DONE Line No: 127
    // TODO @blaji token minted, approval given to FUnder , then funder will pull in the token into itself
    // (link) //DONE Line No: 61-64
    //TODO @balaji the money should go to the Beneficiary, the beneficiary has the right to remove the fundings

    /**
    * @dev Linking ERC721 with the FunderEscrow
    */
    function link(address _owner, uint256 _tokenId) public {
        ART_TOKEN.safeTransferFrom(_owner, address(this), _tokenId);
        assert(ART_TOKEN.ownerOf(_tokenId) == address(this)); // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
        emit FunderLinked(_owner, address(this), now);
    }
    //The ERC721 will be owned by FUnderEscrow
    // After the funding period ends erc721 is transferred to beneficiary
    /**
    * @dev Mapping the ERC721 Token with the funder's account address
    * @param _numberOfPrints Total number of prints
    */
    function processFundingRequest(uint256 _numberOfPrints) public {
        require(
            funderIndex < MAX_NUMBER_OF_PRINTS,
            "in FunderEscrow:processFundingRequest(). All prints have been funded."
        );
        uint256 noOfPrintsPlusFunderIndex = _numberOfPrints.add(funderIndex);
        if(noOfPrintsPlusFunderIndex > MAX_NUMBER_OF_PRINTS) {
            _numberOfPrints = MAX_NUMBER_OF_PRINTS.sub(funderIndex);
        }
        for(uint256 i = 0; i < _numberOfPrints; i++) {
            require(
                fundPrints(msg.sender, PLEDGE_AMOUNT),
                "in FunderEscrow:processFundingRequest(). Error while receiving the pledge amount."
            );
        }
    }

    /**
    * @dev Function that approves other addresses to spend the ERC721 owned by FunderEscrow
    * @param _tokenId The ERC721 token's ID.
    * @param _addressToBeApproved The address which will spend the ERC721 token on behalf of the owner
    */
    function approveToSpendERC721(address _addressToBeApproved, uint256 _tokenId) onlyOwner public {
        ART_TOKEN.approve(_addressToBeApproved, _tokenId);
    }

    /**
    * @dev On sale of Print, ensures, repayment occurs in FIFO manner
    * @param _soldBy Boolean indicating whether the print was sold by AEL or Third Party
    */
    // TODO @balaji make it external, and also a modifier that checks the signature of SalesContract
    function onSpecificPerformance(bool _soldBy) external {
        address _funder = FIFOFunder[printSoldIndex];
        // Pay to the _beneficiary
        payout(_funder, _soldBy);
        // Increase the funderIndex s.t. on next sale, funder at the next index gets the sale amount
        printSoldIndex = printSoldIndex.add(1);
    }

    /**
    * @dev Function that gets the locked in tokens in case if the tokens are locked in the smart contract
    * @param _receiver Address that receives the locked in tokens into this contract
    */
    function getLockedTokens(address _receiver) onlyOwner public {
        uint256 contractBalance = ASSET_TOKEN.balanceOf(address(this));
        require(
            transferTokens(_receiver, contractBalance),
                "in FunderEscrow:getLockedTokens().Trasfer failed."
        ); // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
    }

    /**
    * @dev Transfers exit clause payouts to the funders in case the contract has expired
    */
    function transferExitClausePayout() onlyOwner hasContractExpired public {
        address funder;
        for(uint256 i = printSoldIndex; i < funderIndex; i++) {
            funder = FIFOFunder[i];
            printSoldIndex = printSoldIndex.add(1);
            require(
                transferTokens(funder, EXIT_CLAUSE_PAYOUT_FROM_AEL),
                    "in FunderEscrow:trasferExitClausePayout().Trasfer failed."
            );
            hasFunderBeenPaid[FIFOFunder[i]] = true;
        }
    }

    /**
    * @dev Exit clause additional payment to buy
    * @param _buyer The address to whom the ERC721 has to transferred
    */
    //TODO: @baljai informs Chaitanya why the onlyOwner tag was removed? and why this function was made external
    function
    exitClauseAdditionalPaymentToBuy(address _buyer, uint256 _printId)
    hasContractExpired
    external returns (bool)
    {
        printSoldIndex = printSoldIndex.add(1);
        require(
            transferTokensFrom(
                _buyer, AEL_GALLERY_ADDRESS, EXIT_CLAUSE_ADDITIONAL_PAYMENT_TO_BUY
            ),
                "in FunderEscrow:exitClauseAdditionalPaymentToBuy().Trasfer from failed."
        );
        return true;
    }

    /**
   * @dev Gets tokens from the funder into this contract
   * @param _funder The funders account address
   * @param _pledgeAmount The amount pledged by the funder for the Print(ERC721 Token)
   */
    function fundPrints(address _funder, uint256 _pledgeAmount) public returns (bool) {
        // Transfers the pledgeAmount to the beneficiary
        address beneficiary = beneficiary();
        require(
            transferTokensFrom(_funder, beneficiary, _pledgeAmount),
            "in FunderEscrow:fundPrints().Trasfer failed from funder to beneficiary."
        );
        FIFOFunder[funderIndex] = _funder;
        funderIndex = funderIndex.add(1);
        emit PrintFunded(_funder, _pledgeAmount, now);
        return true;
    }

    /**
    * @dev Function that proceeds with the payouts
    * @param _funder The funders account address
    * @param _soldBy Boolean indicating whether the print was sold by AEL or Third Party
    */
    function payout(address _funder, bool _soldBy) internal {
        // Pull in the tokens into the FunderEscrow
        // transferTokensFrom(msg.sender, address(this), PLEDGE_AMOUNT);
        // _soldBy == 1 implies AEL else John Kempner
        // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
        if(_soldBy) {
            require(
                transferTokens(AEL_GALLERY_ADDRESS, GALLERY_FEE_UPON_SALE),
                    "in FunderEscrow:payout().Trasfer failed for gallery."
            );
            require(
                transferTokens(AEL_COMMISSION_ADDRESS, AEL_COMMISSION),
                    "in FunderEscrow:payout().Trasfer failed for commission."
            );
            require(
                transferTokens(_funder, INVESTOR_RETURN_ON_PLEDGE_AMOUNT_UPON_SALE),
                    "in FunderEscrow:payout().Trasfer failed for funder."
            );
            hasFunderBeenPaid[_funder] = true;
        } else {
            require(
                transferTokens(THIRD_PARTY_GALLERY_ADDRESS, GALLERY_FEE_UPON_SALE),
                    "in FunderEscrow:payout().Trasfer failed for third party gallery."
            );
            require(
                transferTokens(AEL_COMMISSION_ADDRESS, AEL_COMMISSION),
                    "in FunderEscrow:payout().Trasfer failed for commission for third party gallery."
            );
            require(
                transferTokens(_funder, INVESTOR_RETURN_ON_PLEDGE_AMOUNT_UPON_SALE),
                    "in FunderEscrow:payout().Trasfer failed for funder for third party gallery."
            );
            hasFunderBeenPaid[_funder] = true;
        }
    }

    /**
    * @dev Function that transfers the tokens.
    * @param _receiver The receiver's address.
    * @param _amount The amount to transfer.
    */
    function transferTokens(address _receiver, uint256 _amount) internal returns (bool) {
        return ASSET_TOKEN.transfer(_receiver, _amount);
    }

    /**
    * @param _from The address from whom the tokens have to be pulled in.
    * @param _to The address that will spend the tokens on behalf of the _sender.
    * @param _amount The amount of tokens to be pulled in.
    */
    function transferTokensFrom(address _from, address _to, uint256 _amount) internal returns (bool) {
        return ASSET_TOKEN.transferFrom(_from, _to, _amount);
    }

}

