/**
* Create by Balaji on 04/01/2019
*/
pragma solidity 0.5.2;
import "./ERC721/ArtToken.sol";
import "./ERC20/AssetToken.sol";
import "openzeppelin-solidity/contracts/utils/Address.sol";
import "./FunderEscrow.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/token/ERC721/ERC721Holder.sol";
/**
* @author Balaji Pachai
* @title SalesContract
* @dev SalesContract that is intended to facilitate the trade of ERC721 tokens
*/


contract SalesContract is Ownable, ERC721Holder {
    using SafeMath for uint256;
    using Address for address;

    ArtToken public erc721Address;
    AssetToken public assetTokenAddress;
    FunderEscrow public funderEscrowAddress;
    SecondaryState public secondaryState;

    bool public atomicTransferStatus;

    enum SecondaryState { Start, BuyRequest, PayTaxAndShipment, Sold }

    mapping(address => uint256[]) public assetsOfOwner;
    mapping(uint256 => Terms) public assetTerms;

    event AssetOwnershipTransferred(
        address indexed oldOwner,
        address indexed newOwner,
        uint indexed basePrice,
        uint timestamp
    );

    modifier validState(SecondaryState _state) {
        require(secondaryState == _state, "in SalesContract:validState(). States do not match");
        _;
    }

    struct Terms {
        uint256 _basePrice;
        uint256 _estimatedTaxAndShipmentAmount;
        bool _isSold;
        bytes32 _shippingHash;
    }

    /**
    * @dev Constructor.
    * @param addressNFT The address of the ERC721 contract.
    * @param addressFT The address of the ERC20 contract.
    */
    //TODO @balaji coverage breaks at line No: 44:33, identify it
    constructor(
        address addressNFT,
        address addressFT,
        uint256 tokenId,
        uint256 basePrice,
        address tokenOwner
    ) public {
        require(addressNFT != address(0), "in SalesContract:constructor(). addressNFT cannot be address(0)");
        require(addressFT != address(0), "in SalesContract:constructor(). addressFT cannot be address(0)");
        erc721Address = ArtToken(addressNFT);
        assetTokenAddress = AssetToken(addressFT);
        setAssetDetails(tokenId, basePrice, tokenOwner);
    }

    function setFunderEscrowAddress(address _contractAddress) public onlyOwner {
        require(_contractAddress.isContract(), "in SalesContract:setFunderEscrowAddress(). Provided address is EOA.");
        funderEscrowAddress = FunderEscrow(_contractAddress);
    }
    /**
    * @dev Function that sets the asset details for the given ERC721 tokenId
    * @param tokenId The ERC721 token's ID.
    * @param basePrice The base price of the asset excluding the taxes and shipment charges.
    */
    function setAssetDetails (uint256 tokenId, uint256 basePrice, address _tokenOwner) public {
        require(
            _tokenOwner == erc721Address.ownerOf(tokenId),
            "in SalesContract:setAssetDetails(). msg.sender is not the owner of asset"
        );
        require(basePrice > 0, "in SalesContract:setAssetDetails(). basePrice is not greater than 0");
        uint256 length;
        if (!_tokenOwner.isContract()) {
            assetsOfOwner[msg.sender].push(tokenId);
            length = assetsOfOwner[msg.sender].length.sub(1);
        } else {
            assetsOfOwner[_tokenOwner].push(tokenId);
            length = assetsOfOwner[_tokenOwner].length.sub(1);
        }
        assetTerms[length] = Terms({
            _basePrice: basePrice,
            _isSold: false,
            _estimatedTaxAndShipmentAmount: 0,
            _shippingHash: bytes32(0)
        });
        //TODO @balaji Add the assets into the bucket here
        secondaryState = SecondaryState.Start;
    }


    /**
    * Function to increase the basePrice of the Asset.
    * @param _newBasePrice The new base price for the asset.
    * @param _tokenId The ERC721 token's ID.
    */
    //TODO @balaji How to get this into FunderEscrow?
    function increaseBasePrice(uint256 _newBasePrice, uint256 _tokenId) public validState(SecondaryState.Start) {
        uint256 index;
        uint256 basePrice;
        bool isSold;
        uint256 estimatedTaxAndShipment;
        bytes32 shippingHash;
        require(
            msg.sender == erc721Address.ownerOf(_tokenId),
            "in SalesContract:increaseBasePrice(). msg.sender is not the owner of asset"
        );
        ( basePrice, isSold, index, estimatedTaxAndShipment, shippingHash) = getTerms(msg.sender, _tokenId);
        assetTerms[index]._basePrice = _newBasePrice;
    }

    /**
    * @dev Function that initiates the buying of an asset(ERC721 token to be specific).
    * @param _estimatedTaxAndShipmentAmount The estimation of tax + shipment amount.
    * @param _tokenId The ERC721 token's ID.
    */
    function buy(
        uint256 _estimatedTaxAndShipmentAmount,
        uint256 _tokenId,
        bool _soldBy
    ) public validState(SecondaryState.Start) {
        require(msg.sender != address(0), "in SalesContract:buy(). The buyer cannot be address(0)"); // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
        address owner = erc721Address.ownerOf(_tokenId);
        uint256 index;
        uint256 basePrice;
        bool isSold;
        uint256 estimatedTaxAndShipment;
        bytes32 shippingHash;
        (basePrice, isSold, index, estimatedTaxAndShipment, shippingHash) = getTerms(owner, _tokenId);
        require(!isSold, "The requested asset is already sold"); // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
        assetTerms[index]._estimatedTaxAndShipmentAmount = _estimatedTaxAndShipmentAmount;
        atomicTransfer(msg.sender, basePrice, owner, _tokenId, index, _soldBy) ;
    }

    /**
    * @dev Function that pulls in the tax and shipment amount into the escrow.
    * @param _buyer The address of the buyer.
    * @param _tokenId The ERC721 token's ID.
    * @param _amount The sum of tax and shipment amount.
    */
    function getTaxAndShipmentAmount(
        address _buyer, uint256 _tokenId, uint256 _amount, bytes32 _hashOfShipmentDetails, address _shippingAgency
    )
    public
    validState(SecondaryState.PayTaxAndShipment)
    {
        uint256 index;
        uint256 basePrice;
        bool isSold;
        uint256 estimatedTaxAndShipment;
        bytes32 shippingHash;
        address owner = erc721Address.ownerOf(_tokenId);
        (basePrice, isSold, index, estimatedTaxAndShipment, shippingHash) = getTerms(owner, _tokenId);
        require(
            assetTerms[index]._estimatedTaxAndShipmentAmount >= _amount,
                "in SalesContract:getTaxAndShipmentAmount(). Estimated tax + shipment amount is less"
        );

        if (transferTokensFrom(_buyer, address(this), _amount) == true) {
            if (transferTokens(_shippingAgency, _amount) == true) {
                shipAsset(_tokenId, _hashOfShipmentDetails);
            }
        }
    }

    /**
    * Function that sells the print to the funder in case of exit clause additional payment to buy
    */
    function buyExitClause(address _buyer, uint256 _tokenId, uint256 _amount) public {
        uint256 exitClauseAmount = funderEscrowAddress.EXIT_CLAUSE_PAYOUT_FROM_AEL();
        require(_amount == exitClauseAmount, "in SalesContract:buyExitClause(). Insufficient exit clause amount");
        require(
            funderEscrowAddress.exitClauseAdditionalPaymentToBuy(_buyer, _tokenId),
                "in SalesContract:buyExitClause(). Failed in FunderEscrow."
        );
        address owner = erc721Address.ownerOf(_tokenId);
        uint256 index;
        uint256 basePrice;
        bool isSold;
        uint256 estimatedTaxAndShipment;
        bytes32 shippingHash;
        ( basePrice, isSold, index, estimatedTaxAndShipment, shippingHash) = getTerms(owner, _tokenId);
        transferAsset(owner, _buyer, _tokenId, index);
    }

    /**
    * Function that gets the assets details of the provided _tokenId
    * @param _tokenId The ERC721 token's ID.
    */
    function getAssetDetails(uint256 _tokenId) public view returns (uint256, bool, uint256, uint256, bytes32) {
        address owner = erc721Address.ownerOf(_tokenId);
        uint256 index;
        uint256 basePrice;
        bool isSold;
        uint256 estimatedTaxAndShipment;
        bytes32 shippingHash;
        ( basePrice, isSold, index, estimatedTaxAndShipment, shippingHash) = getTerms(owner, _tokenId);
        return (basePrice, isSold, index, estimatedTaxAndShipment, shippingHash);
    }

    /**
        * @dev Function that ships the asset from the owner to the buyer.
        * @param _tokenId The ERC721 token's ID.
        * @param _hashOfShipmentDetails The hash of shipment details.
        */
    function shipAsset(
        uint256 _tokenId, bytes32 _hashOfShipmentDetails
    )
    internal
    validState(SecondaryState.PayTaxAndShipment) {
        address owner = erc721Address.ownerOf(_tokenId);
        uint256 index;
        uint256 basePrice;
        bool isSold;
        uint256 estimatedTaxAndShipment;
        bytes32 shippingHash;
        ( basePrice, isSold, index, estimatedTaxAndShipment, shippingHash) = getTerms(owner, _tokenId);
        assetTerms[index]._shippingHash = _hashOfShipmentDetails;
        secondaryState = SecondaryState.Sold;
    }

    /**
    * @dev Internal function to pull in tokens into the escrow.
    * @param _buyer The address from whom the tokens have to be pulled in.
    * @param _amount The amount of tokens to be pulled in.
    * @param _owner The owner to whom the tokens has to be transferred from the escrow.
    * @param _tokenId The ERC721 token's ID.
    */
    function atomicTransfer(
        address _buyer, uint256 _amount, address _owner, uint256 _tokenId, uint256 _tokenIndex, bool _soldBy
    )
    internal
    returns (bool) {
        // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
        require(
            address(this) == erc721Address.getApproved(_tokenId)
            || erc721Address.isApprovedForAll(_owner, address(this)),
            "in SalesContract:atomicTransfer(). SalesContract is neither approved nor an operator"
        );

        require(
            assetTokenAddress.allowance(_buyer, address(this)) >= _amount,
                "in SalesContract:atomicTransfer(). Insufficient allowance"
        );

        require(
            transferTokensFrom(_buyer, _owner, _amount),
                "in SalesContract:atomicTransfer(). Token transferred failed"
        );

        secondaryState = SecondaryState.BuyRequest;

        require(
            transferAsset(_owner, _buyer, _tokenId, _tokenIndex),
                "in SalesContract:atomicTransfer(). Failed to transfer asset"
        );

        emit AssetOwnershipTransferred(_owner, _buyer, _amount, now);

        require(address(funderEscrowAddress) != address(0), "Please set funderEscrow address");
        if(_owner.isContract()) {
            funderEscrowAddress.onSpecificPerformance(_soldBy);
        }
        return true;
    }

    /**
    * @dev Function that transfers the ERC721 Token
    * @param _owner The owner of the asset
    * @param _buyer The address to whom the asset will be transferred
    * @param _tokenId The unique ERC721 asset ID
    */
    function transferAsset(
        address _owner, address _buyer, uint256 _tokenId, uint256 _tokenIndex
    ) internal returns (bool){
        erc721Address.safeTransferFrom(_owner, _buyer, _tokenId);
        assert(erc721Address.ownerOf(_tokenId) == _buyer); // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
        assetTerms[_tokenIndex]._isSold = true;
        secondaryState = SecondaryState.PayTaxAndShipment;
        return true;
    }

    /**
    * @dev Function that transfers the tokens.
    * @param _receiver The receiver's address.
    * @param _amount The amount to transfer.
    */
    function transferTokens(address _receiver, uint256 _amount) internal returns (bool) {
        return assetTokenAddress.transfer(_receiver, _amount);
    }

    /**
    * @param _from The address from whom the tokens have to be pulled in.
    * @param _to The address that will spend the tokens on behalf of the _sender.
    * @param _amount The amount of tokens to be pulled in.
    */
    function transferTokensFrom(address _from, address _to, uint256 _amount) internal returns (bool) {
        return assetTokenAddress.transferFrom(_from, _to, _amount);
    }

    /**
    * @dev Function that gets the terms details such as _basePrice & _isSold.
    * @param owner The address of the owner of the assets.
    * @param tokenId The ERC721's token id.
    * @return Returns the basePrice and isSold details of the asset.
    */
    function getTerms(address owner, uint256 tokenId) internal view returns (uint256, bool, uint256, uint256, bytes32) {
        uint256 index;
        uint256 length = assetsOfOwner[owner].length;
        for (uint256 i = 0; i < length; i++) {
            // Code coverage else path cannot be covered, Why? Have a look at CoverageReportReadme.md in the root dir
            if (assetsOfOwner[owner][i] == tokenId) {
                index = i;
                break;
            }
        }
        uint256 basePrice = assetTerms[index]._basePrice;
        bool isSold = assetTerms[index]._isSold;
        uint256 estimatedTaxAndShipmentAmount = assetTerms[index]._estimatedTaxAndShipmentAmount;
        bytes32 shippingHash = assetTerms[index]._shippingHash;
        return (basePrice, isSold, index, estimatedTaxAndShipmentAmount, shippingHash);
    }
}

